import { Fragment, useState } from "react";
import {
  BrowserRouter,
  Route,
  RouterProvider,
  Routes,
  createBrowserRouter,
} from "react-router-dom";
import MainHeader from "./components/MainHeader/MainHeader";
import Login from "./components/pages/Login";
import Home from "./components/pages/Home";

//const pageRouter= createBrowserRouter([{path: '/login', element: <Login/>},{path: '/home', element:<Home/>}])

function App() {
  const [loggedIn, setLoggedIn] = useState(false);

  function loginHandler(email, password) {
    setLoggedIn(true);
  }

  function logoutHandler() {
    setLoggedIn(false);
  }

  return (
    <BrowserRouter>
      <Routes>
        <Route
          path="/login"
          element={<MainHeader isLogged={loggedIn} onLogout={logoutHandler} />}
        >
          <Route
            index
            element={!loggedIn && <Login onLogin={loginHandler} />}
          />
          <Route path="/home" element={loggedIn && <Home />} />
        </Route>
      </Routes>
    </BrowserRouter>
  );
}

export default App;
