import { useRef, useState } from "react";

function Dashboard() {
  const [gender, setGender] = useState();
  const [nationality, setNationality] = useState("");

  const inputEmail = useRef();
  const inputName = useRef();
  const inputNumber = useRef();
  const inputAddress = useRef();

  function detailsSubmitHandler(event) {
    event.preventDefault();
    console.log(
      inputName.current.value,
      inputEmail.current.value,
      inputNumber.current.value,
      gender,
      inputAddress.current.value,
      nationality
    );
    inputName.current.value = "";
    inputAddress.current.value = "";
    inputNumber.current.value = "";
    inputEmail.current.value = "";
    setGender(null);
    setNationality("");
  }

  function nationalityHandler(event) {
    setNationality(event.target.value);
  }

  function genderHandler(event) {
    setGender(event.target.value);
  }
  return (
    <form onSubmit={detailsSubmitHandler}>
      <div>
        <label htmlFor="name">Full Name</label>
        <input type="text" id="name" ref={inputName}></input>
      </div>
      <div>
        <label htmlFor="email">Email</label>
        <input type="email" id="email" ref={inputEmail}></input>
      </div>
      <div>
        <label htmlFor="phno">Phone Number</label>
        <input type="number" id="phno" ref={inputNumber}></input>
      </div>
      <div>
        <p>Select your gender</p>
        <input
          type="radio"
          name="gender"
          id="male"
          value="Male"
          onChange={genderHandler}
        />
        <label htmlFor="male">Male</label>
        <input
          type="radio"
          name="gender"
          id="female"
          value="Female"
          onChange={genderHandler}
        />
        <label htmlFor="female">Female</label>
      </div>
      <div>
        <label htmlFor="address">Address</label>
        <input type="text" id="address" ref={inputAddress}></input>
      </div>
      <div>
        <label htmlFor="dropdown">Nationality</label>
        <select id="dropdown" value={nationality} onChange={nationalityHandler}>
          <option value="">-- Select --</option>
          <option value="India">India</option>
          <option value="Bangladesh">Bangladesh</option>
          <option value="Pakistan">Pakistan</option>
          <option value="Nepal">Nepal</option>
        </select>
      </div>
      <div>
        <button type="submit">Submit</button>
      </div>
    </form>
  );
}

export default Dashboard;
