import { Fragment, useState } from "react";
import { RouterProvider, createBrowserRouter } from "react-router-dom";
import MainHeader from "./components/MainHeader/MainHeader";
import Login from "./components/pages/Login";
import Home from "./components/pages/Home";

//const pageRouter= createBrowserRouter([{path: '/login', element: <Login/>},{path: '/home', element:<Home/>}])
const PageRouter= createBrowserRouter();
function App() {
  const [loggedIn, setLoggedIn] = useState(false);

  function loginHandler(email, password) {
    setLoggedIn(true);
  }

  function logoutHandler() {
    setLoggedIn(false);
  }

  return (
    <RouterProvider router={PageRouter}>
      <PageRouter>
        <div>
          <MainHeader isLogged={loggedIn} onLogout={logoutHandler} />
          <PageRouter.Route path="/login" exact>
            {!loggedIn && <Login onLogin={loginHandler} />}
          </PageRouter.Route>
          <PageRouter.Route path="/home" exact>
            {loggedIn && <Home />}
          </PageRouter.Route>
        </div>
      </PageRouter>
    </RouterProvider>
  );
}
