import { Fragment, useRef, useState } from "react";
import Button from "../UI/Button/Button";
import Card from "../UI/Card/Card";

function Login(props) {
 const [formIsValid,setFormIsValid]= useState(false);
 const InputEmailRef= useRef();
 const InputPasswordRef= useRef();
 

function submitHandler(event){
    event.preventDefault();
    const enteredEmail=InputEmailRef.current.value;
    const enteredPassword=InputPasswordRef.current.value;
    if(enteredEmail !== 'samuel@react.com' || enteredPassword.trim().length < 5){
       setFormIsValid(true);
     return;
    }
    props.onLogin(enteredEmail,enteredPassword);
    InputEmailRef.current.value='';
    InputPasswordRef.current.value='';
    }

    function errorHandler(){
        setFormIsValid(false);
    }

  return (<Fragment>
    {formIsValid && (<Card><Button onClick={errorHandler}>Invalid Input</Button></Card>)}
    <Card>
      <form onSubmit={submitHandler}>
        <label htmlFor="email">Email</label>
        <input type="email" id="email" ref={InputEmailRef} />
        <label htmlFor="pass">Password</label>
        <input type="password" id="pass" ref={InputPasswordRef} />
        <Button type="submit">
          Login
        </Button>
      </form>
    </Card>
    </Fragment>
  );
}

export default Login;
