import { useEffect, useState } from "react";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import Login from "./components/pages/Login";
import Home from "./components/pages/Home";

function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [isuemail, setisuemail]= useState('');

  useEffect(() => {
    const checkLoggedin = localStorage.getItem("isLoggedIn");
    if (checkLoggedin === "1") setLoggedIn(true);
    setisuemail(localStorage.getItem('email'));
  }, []);

  function loginHandler(uemail, upassword) {
    setLoggedIn(true);
    localStorage.setItem("isLoggedIn", "1");
    localStorage.setItem('email',uemail);
    setisuemail(uemail);

  }

  function logoutHandler() {
    localStorage.removeItem("isLoggedIn");
    localStorage.removeItem('email');
    setLoggedIn(false);
  }
  return (
    <BrowserRouter>
      <Routes>
        <Route
          path="/"
          element={!loggedIn && <Login onLogin={loginHandler} />}
        ></Route>
        <Route
          path="/home"
          element={loggedIn && <Home email={isuemail} onLogout={logoutHandler} />}
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
